mapper.select:
select
`clientType`, `uid`, `gameId`, `platformId`, `zoneId`, `data` , `roleData`
from b_behavior_user
where
clientType = #{clientType}
AND uid = #{uid}
AND gameId = #{gameId}
AND platformId = #{platformId}

    AND zoneId = #{zoneId}

mapper.update:
    update 
    b_behavior_user
    set 
    	`data` = #{data,jdbcType=LONGVARCHAR},`roleData` = #{roleData,jdbcType=LONGVARCHAR}
    where 
    	clientType = #{clientType}
	    AND uid = #{uid} 
	    AND gameId = #{gameId}
	    AND platformId = #{platformId}
	    AND zoneId = #{zoneId}
